
$(document).ready(function(){$('div.fprop span.caption').click(function(){if($(this).hasClass('addnew')){var ctl=$(this).parents('div.fprop:eq(0)');var tmp=ctl.clone(true);tmp.find('.nextid').each(function(){$(this).attr('name',$(this).attr('name').replace('node_content_fields[','node_content_fields[1'));});ctl.parent().append(tmp);$(this).removeClass('addnew');}
$('table.fprops',$(this).parent().parent()).css('display','none');$('table.fprops',$(this).parent()).css('display','block');});$('tr.fprop.type select').change(function(){$(this).parents('div.fprop:eq(0)').attr('class','fprop '+$(this).val());});$('tr.fprop.name input').change(function(){$('span.caption',$(this).parents('div.fprop:eq(0)')).html($(this).val());});$('tr.fprop.delete input').change(function(){$('span.caption',$(this).parents('div.fprop:eq(0)')).css('text-decoration',$(this).attr('checked')?'line-through':'underline');});});